import json
from datetime import datetime, timedelta



def format_keywords(keywords):
    # Check if keywords is a list; if so, join it into a single string
    if isinstance(keywords, list):
        return ", ".join(keywords)
    # If keywords is already a string, return it directly
    elif isinstance(keywords, str):
        return keywords
    # If keywords is None or another type, return an empty string
    return ""

