def validate_data(news_data):
    for data in news_data:
        data['media_type'] = get_media_type(data['source'])
        if data.get('meta_description') is None:
            print(data['url'],"- meta_description data missing")
            data['meta_description'] = ""

        if data.get('last_scraped') is None:
            print(data['url'],"- last_scraped data missing")
            data['last_scraped'] = datetime.now()

        if data.get('international') is None:
            print(data['url'],"- international data missing")
            data['international'] = False

        if data.get('sentiment') is None:
            print(data['url'],"- sentiment data missing")
            data['sentiment'] = "Neutral"

        if data.get('views') is None:
            print(data['url'],"- views data missing")
            data['views'] = 0

        if data.get('news_score') is None:
            print(data['url'],"- news_score data missing")
            data['news_score'] = 0

        if data.get('rating') is None:
            print(data['url'],"- rating data missing")
            data['rating'] = 0

        if data.get('engagement') is None:
            print(data['url'],"- engagement data missing")
            data['engagement'] = 0

        if data.get('author') is None:
            print(data['url'],"- author data missing")
            data['author'] = ""
        
        if data.get('content') is None:
            print(data['url'],"- content data missing")
            data['content'] = ""

        if data.get('keywords') is None:
            print(data['url'],"- keywords data missing")
            data['keywords'] = ""

        else:
            data['keywords'] = format_keywords(data['keywords'])

        if data.get('updated_date') is None:
            print(data['url'],"- updated_date data missing")
            data['updated_date'] = None

        
        

        if data.get('news_subcategory') is None or data['news_subcategory']=="":
            print(data['url'],"- news_subcategory data missing")
            data['news_subcategory'] = (data['news_type'])

        if data.get('image_urls') is None or data['image_urls']=="":
            print(data['url'],"- image_urls data missing")
            data['image_urls'] = get_image_url(data['source'])

        if data.get('old') is None or data['old']=="":
            print(data['url'],"- old data missing")
            # Determine if news is older than 4 days
            published_date = datetime.fromisoformat(data["published_date"])
            data['old'] = (datetime.now() - published_date) > timedelta(days=4)
    return news_data



with open("final_data.json", 'r', encoding='utf-8') as file:
    news_data = json.load(file)

# Filter out entries that lack a title, source, or published date
filtered_news_data = [
    data for data in news_data 
    if data.get('title') and data.get('title') != "" 
    and data.get('source') is not None 
    and data.get('published_date') is not None
    and data.get('news_type') is not None
]

validated_data = validate_data(filtered_news_data)
print(len(news_data), len(filtered_news_data))

# Save the transformed data to a new JSON file
with open('final_data.json', 'w', encoding='utf-8') as file:
    json.dump(validated_data, file, ensure_ascii=False, indent=4)
